    /**
     * @param Ethereum $chain
     * @param AddressInterface|null $address
     * @param array|null $event
     * @return ERC721
     */
    public function make(Ethereum $chain, AddressInterface $address = null, array $event = null): ERC721
    {
        if (empty($this->ERC721)) {
            if (empty($event)) {
                $event = $this->event;
            }
            if (empty($address)) {
                $address = $chain->getAddress(AddressCode::ZERO_ADDRESS);
            }
            $this->ERC721 = new ERC721($chain, new ContractConfig($address, $this->abi, $event));
        }
        $ERC721 = clone $this->ERC721;
        if ($address && !$this->ERC721->getContractAddress()->compare($address->toString())) {
            $ERC721->setContractAddress($address);
        }
        return $ERC721;
    }
